sp_widget.script:
(function() {
  var user = gs.getUser();

  data.userName = user.getDisplayName();

  // Get current active sprint
  var gr = new GlideRecord('x_1865669_retrospe_sprint');
	gr.addEncodedQuery('stateINin_progress,ready_for_submission,ready_for_review')
  gr.orderByDesc('start_date');
  gr.setLimit(1);
  gr.query();


  if(gr.next()) {
    data.currentSprint = gr.sprint_name.toString();
    data.currentSprintId = gr.sys_id.toString();
    data.sprintState = gr.state.toString();
  } else {
    data.currentSprint = 'No active sprint';
    data.sprintState = 'none';
  }
})();
