sp_widget.client_script:
function($scope, $window) {
  var c = this;
  // Header banner - no client logic needed
}

sp_widget.script:
(function() {
  var user = gs.getUser();
  var userId = user.getID();

  data.userName = user.getDisplayName();

  // 1) Find all teams where this user is a member
  var teamIds = [];
  var teamMemberGr = new GlideRecord('x_1865669_retrospe_team_member');
  teamMemberGr.addQuery('user', userId);
  teamMemberGr.query();

  while (teamMemberGr.next()) {
    var teamId = teamMemberGr.getValue('team');
    if (teamId) {
      teamIds.push(teamId);
    }
  }

  // If user is not in any team, return early
  if (teamIds.length === 0) {
    data.currentSprint = 'No team assigned';
    data.currentSprintId = '';
    data.sprintState = 'none';
    data.sprintReady = false;
    data.alreadySubmitted = false;
    data.noTeam = true;
    return;
  }

  data.noTeam = false;

  // 2) Get current active sprint for user's teams
  var gr = new GlideRecord('x_1865669_retrospe_sprint');
  gr.addEncodedQuery('stateINin_progress,ready_for_submission,ready_for_review');
  gr.addQuery('team', 'IN', teamIds.join(','));
  gr.orderByDesc('start_date');
  gr.setLimit(1);
  gr.query();

  if (gr.next()) {
    data.currentSprint = gr.sprint_name.toString();
    data.currentSprintId = gr.sys_id.toString();
    data.sprintState = gr.state.toString();
    data.sprintReady = true;
    data.teamName = gr.team.getDisplayValue();

    // 3) Check if user already submitted for this sprint
    var retroGr = new GlideRecord('x_1865669_retrospe_retrospective_submission');
    retroGr.addQuery('sprint', gr.sys_id);
    retroGr.addQuery('submitted_by', userId);
    retroGr.query();

    if (retroGr.next()) {
      data.alreadySubmitted = true;
      data.submitBtnText = 'Update Retrospective';
      data.submissionId = retroGr.sys_id.toString();
    } else {
      data.alreadySubmitted = false;
      data.submitBtnText = 'Submit Retrospective';
    }
  } else {
    data.currentSprint = 'No active sprint';
    data.currentSprintId = '';
    data.sprintState = 'none';
    data.sprintReady = false;
    data.alreadySubmitted = false;
  }
})();
