sys_script_client.script:
function onLoad() {

    // Only run on NEW records
    if (!g_form.isNewRecord())
        return;

    // helper: fully lock the form
    function lockFormWithMessage(msg) {
        g_form.addErrorMessage(msg);

        // Make all fields read-only
        var fields = g_form.getEditableFields();
        for (var i = 0; i < fields.length; i++) {
            g_form.setReadOnly(fields[i], true);
        }
		g_form.clearOptions('sprint');


        // Optionally hide buttons in classic UI
        if (g_form.removeSubmitButton)
            g_form.removeSubmitButton();
        if (g_form.removeSaveButton)
            g_form.removeSaveButton();
    }

    // 1. Set Submitted By to current user
    if (!g_form.getValue('submitted_by')) {
        var currentUserId = g_user.userID;
        g_form.setValue('submitted_by', currentUserId);
        g_form.setReadOnly('submitted_by', true);
    }

    // 2. Auto-populate Team
    if (!g_form.getValue('team')) {
        var ga1 = new GlideAjax('RetroTeamHelper');
        ga1.addParam('sysparm_name', 'getUserTeam');
        ga1.addParam('sysparm_user_id', g_user.userID);

        ga1.getXMLAnswer(function(teamSysId) {
            if (teamSysId && teamSysId !== 'null') {
                g_form.setValue('team', teamSysId);
                g_form.setReadOnly('team', true);
            }
        });
    }

    // 3. Auto-populate Sprint (current sprint for user's team)
    if (!g_form.getValue('sprint')) {
        var ga2 = new GlideAjax('RetroTeamHelper');
        ga2.addParam('sysparm_name', 'getCurrentSprint');
        ga2.addParam('sysparm_user_id', g_user.userID);

        ga2.getXMLAnswer(function(sprintSysId) {
            if (sprintSysId && sprintSysId !== 'null') {
                // Check duplicate before setting
                checkForDuplicateRetro(sprintSysId, lockFormWithMessage);
            } else {
                // No sprint ready for submission → lock form
                lockFormWithMessage('No sprint is currently ready for retrospective submission. Please contact your manager.');
            }
        });
    }

    // 4. Welcome message
    var userName = g_user.firstName + ' ' + g_user.lastName;
    g_form.addInfoMessage('Submitting retrospective as: ' + userName);
}


// Check for duplicate and either lock form or set sprint
function checkForDuplicateRetro(sprintSysId, lockFormFn) {
    var ga = new GlideAjax('RetroManagementAjax');
    ga.addParam('sysparm_name', 'checkDuplicateSubmission');
    ga.addParam('sysparm_sprint_id', sprintSysId);

    ga.getXMLAnswer(function(answer) {
        if (answer === 'true') {
            // Duplicate found → lock form (no submit)
            lockFormFn('You have already submitted a retrospective for the current sprint. Only one submission per sprint is allowed.');
        } else {
            // No duplicate – safe to proceed
            g_form.setValue('sprint', sprintSysId);
            g_form.setReadOnly('sprint', true);

            g_form.getReference('sprint', function(ref) {
                if (ref) {
                    g_form.addInfoMessage('Submitting for: ' + ref.sprint_name);
                }
            });
        }
    });
}
