sys_script_client.script:
function onLoad() {

    // Only run on NEW records
    if (g_form.isNewRecord()) {

        // 1. Set Submitted By to current user
        if (!g_form.getValue('submitted_by')) {
            var currentUserId = g_user.userID;
            g_form.setValue('submitted_by', currentUserId);
            g_form.setReadOnly('submitted_by', true);
        }

        // 4. Auto-populate Team
        if (!g_form.getValue('team')) {
            var ga1 = new GlideAjax('RetroTeamHelper');
            ga1.addParam('sysparm_name', 'getUserTeam');
            ga1.addParam('sysparm_user_id', g_user.userID);

            ga1.getXMLAnswer(function(teamSysId) {
                if (teamSysId && teamSysId != '' && teamSysId != 'null') {
                    g_form.setValue('team', teamSysId);
					g_form.setReadOnly('team',true);
                }
            });
        }

        // 5. Auto-populate Sprint (current sprint for user's team)
        if (!g_form.getValue('sprint')) {
            var ga2 = new GlideAjax('RetroTeamHelper');
            ga2.addParam('sysparm_name', 'getCurrentSprint');
            ga2.addParam('sysparm_user_id', g_user.userID);

            ga2.getXMLAnswer(function(sprintSysId) {
                if (sprintSysId && sprintSysId != '' && sprintSysId != 'null') {
                    g_form.setValue('sprint', sprintSysId);
                    g_form.setReadOnly('sprint', true); // Make read-only
                } else {
                    // No current sprint ready for submission
                    g_form.addErrorMessage('No sprint is currently ready for retrospective submission. Please contact your manager.');
                }
            });
        }

        // 6. Show welcome message
        var userName = g_user.firstName + ' ' + g_user.lastName;
        g_form.addInfoMessage('Submitting retrospective as: ' + userName);
    }
}
