sys_script_include.script:
var RetroManagementAjax = Class.create();
RetroManagementAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    checkDuplicateSubmission: function() {
        var sprintId = this.getParameter('sysparm_sprint_id');
        var userId = gs.getUserID();

        var retroGR = new GlideRecord('x_1865669_retrospe_retrospective_submission');
        retroGR.addQuery('sprint', sprintId);
        retroGR.addQuery('submitted_by', userId);
        retroGR.query();

        return retroGR.hasNext().toString();
    },

    markAsValid: function() {
        var sysId = this.getParameter('sysparm_sys_id');

        var gr = new GlideRecord('x_1865669_retrospe_retrospective_submission');

        if (!gr.get(sysId)) {
            return 'error:Record not found';
        }

        if (gr.getValue('status') != 'submitted') {
            return 'error:Only submitted retros can be marked as valid';
        }

        gr.setValue('status', 'valid');
        gr.setValue('reviewed_by', gs.getUserID());
        gr.setValue('reviewed_date', new GlideDateTime());
        gr.update();

        return 'success:Retrospective marked as Valid';
    },

    type: 'RetroManagementAjax'
});
