sys_script_include.script:
var RetroTeamHelper = Class.create();
RetroTeamHelper.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    // Get user's team
    getUserTeam: function() {
        var userId = this.getParameter('sysparm_user_id');

        if (!userId) {
            return '';
        }

        var teamMember = new GlideRecord('x_1865669_retrospe_team_member');
        teamMember.addQuery('user', userId);
        teamMember.addQuery('active', true);
        teamMember.query();

        if (teamMember.next()) {
            return teamMember.team.toString();
        }

        return '';
    },

    // Get team's current sprint (ready for submission)
    getCurrentSprint: function() {
        var userId = this.getParameter('sysparm_user_id');

        if (!userId) {
            return '';
        }

        // First, get user's team
        var teamMember = new GlideRecord('x_1865669_retrospe_team_member');
        teamMember.addQuery('user', userId);
        teamMember.addQuery('active', true);
        teamMember.query();

        if (!teamMember.next()) {
            return '';
        }

        var teamId = teamMember.team.toString();

        // Then, get team's current sprint that's ready for submission
        var sprint = new GlideRecord('x_1865669_retrospe_sprint');
        sprint.addQuery('team', teamId);
        sprint.addQuery('state', 'ready_for_submission');
        sprint.orderByDesc('start_date'); // Get most recent
        sprint.setLimit(1);
        sprint.query();

        if (sprint.next()) {
            return sprint.sys_id.toString();
        }

        return '';
    },

    type: 'RetroTeamHelper'
});
