sys_script_include.script:
var RetroManagerStatsAPI = Class.create();
RetroManagerStatsAPI.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getManagerStats: function() {
        var userId = this.getParameter('sysparm_user_id') || gs.getUserID();

        var stats = {
            activeSprintsCount: 0,
            pendingReviewsCount: 0,
            openTasksCount: 0,
            avgCompletionScore: 0
        };

        // Count active sprints
        var sprintGR = new GlideAggregate('x_retro_mgmt_sprint');
        sprintGR.addQuery('team.manager', userId);
        sprintGR.addQuery('state', 'IN', 'in_progress,ready_for_submission,ready_for_review');
        sprintGR.addAggregate('COUNT');
        sprintGR.query();
        if (sprintGR.next()) {
            stats.activeSprintsCount = parseInt(sprintGR.getAggregate('COUNT')) || 0;
        }

        // Count pending reviews
        var retroGR = new GlideAggregate('x_retro_mgmt_retro_submission');
        retroGR.addQuery('team.manager', userId);
        retroGR.addQuery('status', 'submitted');
        retroGR.addAggregate('COUNT');
        retroGR.query();
        if (retroGR.next()) {
            stats.pendingReviewsCount = parseInt(retroGR.getAggregate('COUNT')) || 0;
        }

        // Count open tasks
        var taskGR = new GlideAggregate('x_retro_mgmt_retro_task');
        taskGR.addQuery('sprint.team.manager', userId);
        taskGR.addQuery('state', 'IN', 'pending,in_progress');
        taskGR.addAggregate('COUNT');
        taskGR.query();
        if (taskGR.next()) {
            stats.openTasksCount = parseInt(taskGR.getAggregate('COUNT')) || 0;
        }

        // Calculate average completion score
        var metricsGR = new GlideAggregate('x_retro_mgmt_sprint_metrics');
        metricsGR.addQuery('sprint.team.manager', userId);
        metricsGR.addAggregate('AVG', 'completion_score');
        metricsGR.query();
        if (metricsGR.next()) {
            var avg = metricsGR.getAggregate('AVG', 'completion_score');
            stats.avgCompletionScore = avg ? Math.round(parseFloat(avg)) : 0;
        }

        return JSON.stringify(stats);
    },

    type: 'RetroManagerStatsAPI'
});
