sys_ui_action.script:
(function() {
    action.setRedirectURL(current);

    // Check if there are valid retros
    var retroCount = new GlideAggregate('x_1865669_retrospe_retrospective_submission');
    retroCount.addQuery('sprint', current.sys_id);
    retroCount.addQuery('status', 'valid');
    retroCount.addAggregate('COUNT');
    retroCount.query();

    var validCount = 0;
    if (retroCount.next()) {
        validCount = parseInt(retroCount.getAggregate('COUNT'), 10);
    }

    if (validCount === 0) {
        gs.addErrorMessage('No valid retrospective submissions found. Please review submissions first.');
        return;
    }

    // Generate tasks ONLY from START items of valid retros
    var retroGR = new GlideRecord('x_1865669_retrospe_retrospective_submission');
    retroGR.addQuery('sprint', current.sys_id);
    retroGR.addQuery('status', 'valid');
    retroGR.query();

    var tasksCreated = 0;
    var retrosProcessed = 0;

    while (retroGR.next()) {
        var retroSysId  = retroGR.getValue('sys_id');
        var submittedBy = retroGR.getValue('submitted_by');
        var tasksForThisRetro = 0;

        // Create tasks for Start items ONLY
        var startText = retroGR.getValue('start_items');
        if (startText) {
            tasksForThisRetro += createTasksFromText(
                retroSysId,
                current.sys_id,
                startText,
                submittedBy,
                'start'
            );
        }

        tasksCreated += tasksForThisRetro;

        // Mark retro as completed after creating tasks
        if (tasksForThisRetro > 0) {
            retroGR.status = 'reviewed';
            retroGR.update();
            retrosProcessed++;
        }
    }

    if (tasksCreated > 0) {
        gs.addInfoMessage(tasksCreated + ' START task(s) created successfully from ' + retrosProcessed + ' retrospectives');
        gs.addInfoMessage(retrosProcessed + ' retrospective(s) marked as completed');
    } else {
        gs.addErrorMessage('No tasks were created from START items. Please check retrospective content.');
    }

    // Helper: create tasks from multiline text
    function createTasksFromText(retroSysId, sprintSysId, text, submittedBy, category) {
        var count = 0;
        var lines = text.split('\n');

        for (var i = 0; i < lines.length; i++) {
            var line = lines[i].trim();
            if (line && line.length > 0) {
                var taskGR = new GlideRecord('x_1865669_retrospe_retrospective_task');
                taskGR.initialize();
                taskGR.setValue('retro_submission', retroSysId);
                taskGR.setValue('sprint', sprintSysId);
                taskGR.setValue('short_description', line.substring(0, 160));
                taskGR.setValue('description', line);
                taskGR.setValue('category', category); // 'start'
                taskGR.setValue('state', '100'); // Pending
                taskGR.setValue('assigned_to', submittedBy);
                taskGR.insert();
                count++;
            }
        }

        return count;
    }
})();
