sys_ui_action.script:
(function () {

    // Safety: only allow when retrospective is in 'valid' state
    if (current.status != 'valid') {
        gs.addErrorMessage('Only valid retros can generate tasks.');
        action.setRedirectURL(current);
        return;
    }

    // Get the Start items text
    var text = current.getValue('start_items');
    if (!text) {
        gs.addErrorMessage('No Start items found on this retrospective.');
        action.setRedirectURL(current);
        return;
    }

    var retroSysId  = current.getUniqueValue();
    var sprintSysId = current.getValue('sprint');
    var submittedBy = current.getValue('submitted_by');

    // Create tasks from each non-empty line in start_items
    var count = createTasksFromText(retroSysId, sprintSysId, text, submittedBy, 'start');

    if (count > 0) {
        gs.addInfoMessage(count + ' Start task(s) created from this retrospective.');
    } else {
        gs.addErrorMessage('No tasks created from Start items. Please check the content.');
    }

    action.setRedirectURL(current);

    function createTasksFromText(retroSysId, sprintSysId, text, submittedBy, category) {
        var created = 0;
        var lines = text.split('\n');

        for (var i = 0; i < lines.length; i++) {
            var line = ('' + lines[i]).trim();
            if (!line)
                continue;

            var taskGR = new GlideRecord('x_1865669_retrospe_retrospective_task');
            taskGR.initialize();
            taskGR.setValue('retro_submission', retroSysId);
            taskGR.setValue('sprint', sprintSysId);
            taskGR.setValue('short_description', line.substring(0, 160));
            taskGR.setValue('description', line);
            taskGR.setValue('category', category);   // 'start'
            taskGR.setValue('state', '100');         // Pending
            taskGR.setValue('assigned_to', submittedBy);
            taskGR.insert();
            created++;
        }

        return created;
    }

})();
